import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaEdit, FaEye, FaTrashAlt } from "react-icons/fa";
import { Link } from "react-router-dom";
import Search from "../common/Search";
import Pagination from "react-paginate";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import "./StudentsView.css"; // Create your custom CSS file for pagination styles

const StudentsView = () => {
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState("");
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    loadStudents();
  }, [currentPage]); // Reload students when the currentPage changes

  const loadStudents = async () => {
    const result = await axios.get(
      `http://localhost:4500/students?page=${currentPage}&size=10`, // Send page and size as query parameters
      {
        validateStatus: () => {
          return true;
        },
      }
    );
    if (result.status === 200) {
      setStudents(result.data.content);
      setTotalPages(result.data.totalPages);
    }
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:4500/students/delete/${id}`);
    loadStudents();
  };

  const handlePageClick = (data) => {
    setCurrentPage(data.selected);
  };

  return (
    <section>
      <Search search={search} setSearch={setSearch} />
      <table className="table table-bordered table-hover shadow">
        <thead>
          <tr className="text-center">
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Department</th>
            <th colSpan="3">Actions</th>
          </tr>
        </thead>

        <tbody className="text-center">
          {students
            .filter((st) =>
              st.firstName.toLowerCase().includes(search)
            )
            .map((student, index) => (
              <tr key={student.id}>
                <th scope="row" key={index}>
                  {index + 1}
                </th>
                <td>{student.firstName}</td>
                <td>{student.lastName}</td>
                <td>{student.email}</td>
                <td>{student.department}</td>
                <td className="mx-2">
                  <Link
                    to={`/student-profile/${student.id}`}
                    className="btn btn-info"
                  >
                    <FaEye />
                  </Link>
                </td>
                <td className="mx-2">
                  <Link
                    to={`/edit-student/${student.id}`}
                    className="btn btn-warning"
                  >
                    <FaEdit />
                  </Link>
                </td>
                <td className="mx-2">
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(student.id)}
                  >
                    <FaTrashAlt />
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      <Pagination
        pageCount={totalPages}
        onPageChange={handlePageClick}
        pageRangeDisplayed={10}
        marginPagesDisplayed={0}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </section>
  );
};

export default StudentsView;
